 Возвращает объект DOMElement либо &null;, если элемент
 не найден.

  &reftitle.examples;

   DOMDocument::getElementById() Example
   &dom.book.example;


<?php

$doc = new DomDocument;

// Необходимо проверить документ на действительность перед тем как 
// ссылаться на id
$doc->validateOnParse = true;
$doc->Load('book.xml');

echo "Элемент с id 'php-basics': " . $doc->getElementById('php-basics')->tagName . "\n";

?>


