    Примеры использования id3_get_version


<?php
$version = id3_get_version( "path/to/example.mp3" );
if ($version & ID3_V1_0) {
    echo "Содержит тег версии 1.x\n";
}
if ($version & ID3_V1_1) {
    echo "Содержит тег версии 1.1\n";
}
if ($version & ID3_V2) {
    echo "Содержит тег версии 2.x\n";
}
?>


