refentry.refsect1:
    &reftitle.examples;



      Объектно-ориентированный стиль


<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Проверка подключения */
if (mysqli_connect_errno()) {
    printf("Ошибка подключения: %s\n", mysqli_connect_error());
    exit();
}

$mysqli->query("CREATE TABLE Language LIKE CountryLanguage Type=InnoDB");

/* Отключение режима автофиксации */
$mysqli->autocommit(FALSE);

/* Вставка некоторых значений */
$mysqli->query("INSERT INTO Language VALUES ('DEU', 'Bavarian', 'F', 11.2)");
$mysqli->query("INSERT INTO Language VALUES ('DEU', 'Swabian', 'F', 9.4)");

/* Фиксация транзакции */
$mysqli->commit();

/* Удаление таблицы */
$mysqli->query("DROP TABLE Language");

/* Закрытие соединения */
$mysqli->close();
?>




      Процедурный стиль


<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "test");

/* Проверка подключения */
if (!$link) {
    printf("Ошибка подключения: %s\n", mysqli_connect_error());
    exit();
}

mysqli_query($link, "CREATE TABLE Language LIKE CountryLanguage Type=InnoDB");

/* Отключение режима автофиксации */
mysqli_autocommit($link, FALSE);

/* Вставка некоторых значений */
mysqli_query($link, "INSERT INTO Language VALUES ('DEU', 'Bavarian', 'F', 11.2)");
mysqli_query($link, "INSERT INTO Language VALUES ('DEU', 'Swabian', 'F', 9.4)");

/* Фиксация транзакции */
mysqli_commit($link);

/* Удаление таблицы */
mysqli_query($link, "DROP TABLE Language");

/* Закрытие соединения */
mysqli_close($link);
?>
