refentry.refsect1:
&reftitle.returnvalues;


 Возвращает &true;, если var является переменной типа float, 
 &false; в противном случае.

     &reftitle.examples;



    Пример использования is_float


<?php
if(is_float(27.25)) {
 echo "действительное число\n";
}else {
 echo "не действительное число\n";
}
var_dump(is_float('abc'));
var_dump(is_float(23));
var_dump(is_float(23.5));
var_dump(is_float(1e7));  //Научная запись
var_dump(is_float(true));
?>


    &example.outputs;


действительное число
bool(false)
bool(false)
bool(true)
bool(true)
bool(false)
