refentry.refsect1:
&reftitle.returnvalues;


 Возвращает &true;, если var является целым числом, 
 &false; в противном случае.

  &reftitle.examples;



    Пример использования is_int


<?php
if (is_int(23)) {
 echo "целое число\n";
} else {
 echo "не целое число\n";
}
var_dump(is_int(23));
var_dump(is_int("23"));
var_dump(is_int(23.5));
var_dump(is_int(true));
?>


    &example.outputs;


целое число
bool(true)
bool(false)
bool(false)
bool(false)
